mod char_property;

mod idna;
mod ucd;

use std::fs;
use std::io;
use std::path::PathBuf;

/// The standard auto-generated source disclaimer.
const PREAMBLE: &'static str = "// WARNING: Auto-generated by unic-gen. DO NOT EDIT MANUALLY!";

pub fn generate_all(mut crates: Vec<String>) -> io::Result<()> {
    crates.sort();

    for krate in &crates {
        let path = tables_path(krate);
        if path.exists() {
            fs::remove_dir_all(&path)?;
        }
        fs::create_dir_all(path)?;
    }

    idna::generate_all(crates.iter().filter(|x| x.starts_with("unic-idna")))?;
    ucd::generate_all(crates.iter().filter(|x| x.starts_with("unic-ucd")))?;

    Ok(())
}

/// Turn a crate name into the path to its tables folder
fn tables_path(package: &str) -> PathBuf {
    let mut path = PathBuf::new();
    for dir in package.split('-') {
        path.push(dir);
    }
    path.push("src/tables");
    path
}
